import React, { useState } from 'react'
import Popup from 'reactjs-popup';
// import GiveReviews from '../GiveReview/GiveReviews';


const ReportTableRow = ({sn,docsName,docsSpecs}) => {
    const [ratingMessage,setRatingMessage] = useState({})
    const [disableReview,setDisableReview] = useState(false)
    const handleReviewSubmit = (eventReviewData) => {
        const {review,rating} = eventReviewData.target
        setRatingMessage({review:review.value, rating:rating.value})
        setDisableReview(true)
        eventReviewData.preventDefault();
    };    
    const [showModal,setShowModal] = useState(false);

    return (
        <tr className=''>
            <td className='text-center pt-3'>{sn}</td>
            <td className='text-center  pt-3'>{docsName}</td>
            <td className='text-center  pt-3'>{docsSpecs}</td>
            {disableReview?
                <td className='my-auto text-wrap text-center '><div className=' btn btn-primary disabled px-5' >View Report</div></td>
            :
                <Popup trigger=
                {
                    <td className='my-auto text-wrap text-center '><div className=' btn btn-primary px-5' onClick={()=>setShowModal(true)}>View Report</div></td>
                } position="center" modal open={showModal} onClose={() => setShowModal(false)}>
                    {(close) => (
                        <div className="" style={{ height: '65vh', overflow: 'scroll',backgroundColor: "#e8eef6" }}>
                            {/* <GiveReviews handleReviewSubmit={handleReviewSubmit} /> */}
                        </div>
                    )}
                </Popup>
            }
            <td className='my-auto text-wrap text-center text-wrap'><div className=' btn btn-primary px-5 text-wrap' >Download Report</div></td>
            
        </tr>
    )
}


export default ReportTableRow;
