import React, { useEffect, useState } from 'react';
import Navbar from '../Navbar/Navbar';



const Notification = ({ children }) => {
  const [isLoggedIn, setIsLoggedIn] = useState(true);
  const [username, setUsername] = useState("");
  const [doctorData, setDoctorData] = useState(null);
  const [appointmentData, setAppointmentData] = useState(null);

  useEffect(() => {
    const storedUsername = sessionStorage.getItem('email');
    const storedDoctorData = JSON.parse(localStorage.getItem('doctorData'));
    const storedAppointmentData = storedDoctorData?.name //JSON.parse(localStorage.getItem(storedDoctorData?.name));
    
    if (storedUsername) {
      setIsLoggedIn(true);
      setUsername(storedUsername);
    }

    if (storedDoctorData) {
        alert("data present")
        // console.log(storedDoctorData.name)
      setDoctorData(storedDoctorData);
    }

    if (storedAppointmentData) {
        console.log("YYYYYYyyyyyye")
        console.log(storedAppointmentData)
      setAppointmentData(storedAppointmentData);
    }
  },[]);
  return (
    <div>
      <Navbar ></Navbar>
      {children}
      {isLoggedIn && appointmentData && (
        <>
          <div className="appointment-card">
            <div className="appointment-card__content">
              <h3 className="appointment-card__title">Appointment Details</h3>
              <p className="appointment-card__message">
                <strong>Doctor:</strong> {doctorData?.drname}                
              </p>
              <p className="appointment-card__message">
              <strong>Specialty:</strong> {doctorData?.specialty}               
              </p>
              <p className="appointment-card__message">
              <strong>Patient:</strong> {doctorData?.name}              
              </p>
              
                
                <strong>Phone Number:</strong> {doctorData?.phone}
                <strong>Date of Appointment:</strong> {doctorData?.date}
                <strong>Time:</strong> {doctorData?.time}
            </div>
          </div>
        </>
      )}
    </div>
  );
};

export default Notification;
