import React, { useState } from 'react'


const GiveReviews = () => {
  const [showForm, setShowForm] = useState(false);
  const [submittedMessage, setSubmittedMessage] = useState('');
  const [showWarning, setShowWarning] = useState(false);
  const [name, setName]=useState("")
  const [rating, setRating]=useState("")
  const [review, setReview]=useState("")
  const [formData, setFormData] = useState({name, review, rating});

  const handleButtonClick = () => {
    setShowForm(true);
  };

  const handleChange = (e) => {
    setFormData(e.target.value);
    console.log(formData)
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    setSubmittedMessage(formData);
    console.log(formData)   
    
    if (formData.name && formData.review && formData.rating > 0) {
        setShowWarning(false);
    } else {
        setShowWarning(true);
    }
    setFormData('');
  };

  return (
    <div className='p-4'>
      <h2>Form with Message</h2>
      {!showForm ? (
        <button onClick={handleButtonClick}>Open Form</button>
      ) : (
        <form onSubmit={handleSubmit}>
                <h2>Give Your Feedback</h2>
                {showWarning && <p className="warning">Please fill out all fields.</p>}
                <div className=''>
                    <label htmlFor="name">Name:</label>
                    <input className='form-control' type="text" id="name" name="name" value={formData.name} onChange={handleChange} />
                </div>
                <div className=''>
                    <label htmlFor="review">Review:</label>
                    <textarea rows="3" className='form-control' id="review" name="review" value={formData.review} onChange={(handleChange)} />
                </div>
                <div>
                    Rating
                </div>
                <button type="submit">Submit</button>
        </form>
      )}
      {submittedMessage && (
        <div>
          <h3>Submitted Message:</h3>
          <p>{submittedMessage}</p>
        </div>
      )}
    </div>
  );
}

export default GiveReviews;
